
public class DoubleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [][]array= {{1,3},{4,7}};
		int i,j;
		for(i=0;i<array.length;i++) {
			for (j = 0; j < array.length; j++)
				System.out.print("  " + array[i][j]);
			System.out.println();

		}

	}

}
